import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Todo } from 'src/models/todo.models';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  public todos: Todo[] = [];
  public title: string = 'Minhas Tarefas';
  public form: any;

  constructor(private fb: FormBuilder) {
    form: FormGroup;
    this.form = this.fb.group({
      title: ['', Validators.compose([
        Validators.minLength(3),
        Validators.maxLength(60),
        Validators.required
      ])]
    });

    // this.todos.push(new Todo(1, 'lavar roupa', true));
    // this.todos.push(new Todo(2, 'lavar roupa', true));
    // this.todos.push(new Todo(3, 'comprar pão', false));
  }

  add(){
    const title = this.form.controls['title'].value;
    const id = this.todos.length + 1;
    this.todos.push(new Todo(id, title, false));

    this.clear();
  }

  clear(){
    this.form.reset();
  }

  remove(todo: Todo){
    const index = this.todos.indexOf(todo);
    if(index !== -1){
      this.todos.splice(index, 1);
    }
  }

  markAsDone(todo: Todo){
    todo.done = true;
  }

  markAsUndone(todo: Todo){
    todo.done = false;
  }

  save(){
    const data = JSON.stringify(this.todos);
    localStorage.setItem('todos', data);
  }

}
<h1>{{title}}</h1>
<ul>
  <p *ngIf="todos.length == 0"><small>Não tem nenhuma tarefa hoje!</small></p>
  <li *ngFor="let todo of todos">
    {{todo.id}} {{todo.title}} |
     <button *ngIf="!todo.done" (click)="markAsDone(todo)">Concluir</button> |
     <button *ngIf="todo.done" (click)="markAsUndone(todo)">Refazer</button> |
     <button (click)="remove(todo)">Remover</button></li>
</ul>

<form [formGroup]="form">
  <fieldset>
    <label>Tarefa</label>
    <input type="text" placeholder="Título da Tarefa" maxlength="60" formControlName="title">
    <small *ngIf="form.controls.title.invalid && !form.controls.title.pristine">Este campo deve conter entre 3 e 60 caracteres</small>
    <button [disabled]="form.invalid" (click)="add()">Salvar</button>
  </fieldset>
</form>
