import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Todo } from 'src/models/todo.models';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  public todos: Todo[] = [];
  public title: string = 'Minhas Tarefas';
  public form: any;

  constructor(private fb: FormBuilder) {
    form: FormGroup;
    this.form = this.fb.group({
      title: ['', Validators.compose([
        Validators.minLength(3),
        Validators.maxLength(60),
        Validators.required
      ])]
    });

    this.todos.push(new Todo(1, 'lavar roupa', true));
    this.todos.push(new Todo(2, 'lavar roupa', true));
    this.todos.push(new Todo(3, 'comprar pão', false));
  }

  remove(todo: Todo){
    const index = this.todos.indexOf(todo);
    if(index !== -1){
      this.todos.splice(index, 1);
    }
  }

  markAsDone(todo: Todo){
    todo.done = true;
  }

  markAsUndone(todo: Todo){
    todo.done = false;
  }
}
<h1>{{title}}</h1>
<ul>
  <li *ngFor="let todo of todos">
    {{todo.id}} {{todo.title}} |
     <button *ngIf="!todo.done" (click)="markAsDone(todo)">Concluir</button> |
     <button *ngIf="todo.done" (click)="markAsUndone(todo)">Refazer</button> |
     <button (click)="remove(todo)">Remover</button></li>
</ul>

<form [formGroup]="form">
  <fieldset>
    <label>Tarefa</label>
    <input type="text">
    <button>Salvar</button>
  </fieldset>
</form>
